import React from "react";
import Navigation from "./Navigation.jsx";
import Smallbutton from "./Smallbutton.jsx";
import img from "../images/pasta.jpg"
import imgheart from "../images/heartwhite.png"

function Viewpost()
{
  return(
  <div class="allposts">
    <Navigation/>
    <br></br>

    <div class="row">

    <div class="info col-lg-6">
    <h1>Pasta</h1>
    <h4>Cuisine</h4>
    </div>

    <div class="col-lg-6">
    <img class="post-img " src={img} alt="food-img"/>
    <br></br><br></br>
    <input type="image" src={imgheart} alt="heart-img"/>
    </div>

    </div>

    <div class="rect">
    <label class="blogText" for=""></label>
    </div>

    <div class="post-button">


    <div class="comment">
    <label for="">Comments</label>
    <br></br><br></br>
    <form class="" action="index.html" method="post">
    <input class="comment-text" type="input" />
    <input type="button" value="post"/>
    </form>
    </div>

    <br></br><br></br>
    <Smallbutton
    name={"Back"}/>

    <br></br><br></br>
    </div>
  </div>
  );
}

export default Viewpost;
